import "core-js/modules/es.array.push.js";
export default {
  name: "Person",
  data() {
    return {
      adminDto: {},
      password: '',
      form: {}
    };
  },
  created() {
    this.load();
  },
  methods: {
    load() {
      this.adminDto = JSON.parse(localStorage.getItem("admin"));
      if (this.adminDto === {}) this.$router.push("/login");
      console.log(this.form);
    },
    save() {
      this.form = {};
      this.form.id = this.adminDto.id;
      this.form.password = this.password;
      this.form.email = this.adminDto.email;
      this.form.mobile = this.adminDto.mobile;
      console.log(this.form);
      console.log(this.form.password);
      this.request.post("http://localhost:9090/admin/save", this.form).then(res => {
        if (res.code === '200') {
          this.$message.success("修改成功");
          //更新LocalStorage
          localStorage.removeItem("admin");
          localStorage.setItem("admin", JSON.stringify(this.adminDto)); //存储信息到浏览器
        } else {
          this.$message.error(res.msg);
        }
      });
    }
  }
};
