import Aside from "@/components/Aside";
import Header from "@/components/Header";
export default {
  name: 'Home',
  data() {
    return {
      tableData: [],
      total: 0,
      pageNum: 1,
      pageSize: 2,
      username: "",
      msg: "hello yang",
      collapseBtnClass: 'el-icon-s-fold',
      isCollapse: false,
      sideWidth: 200,
      logoTextShow: true,
      form: {},
      headerBg: 'headerBg',
      dialogFormVisible: false,
      dialogFormVisible1: false,
      deleteDialogFormVisible: false
    };
  },
  created() {
    // 请求分页查询数据
    this.load();
  },
  components: {
    Aside,
    Header
  },
  methods: {
    collapse() {
      // 点击收缩按钮触发
      this.isCollapse = !this.isCollapse;
      if (this.isCollapse) {
        // 收缩
        this.sideWidth = 64;
        this.collapseBtnClass = 'el-icon-s-unfold';
        this.logoTextShow = false;
      } else {
        // 展开
        this.sideWidth = 200;
        this.collapseBtnClass = 'el-icon-s-fold';
        this.logoTextShow = true;
      }
    },
    load() {
      this.request.get("http://localhost:9090/user/page", {
        params: {
          pageNum: this.pageNum,
          pageSize: this.pageSize
        }
      }).then(res => {
        console.log(res);
        this.tableData = res.data;
        this.total = res.total;
      });
    },
    save() {
      console.log(this.form);
      this.request.post("http://localhost:9090/user/insert", this.form).then(res => {
        if (res) {
          this.$message.success("保存成功");
          console.log(res);
          this.dialogFormVisible = false;
        } else {
          this.$message.error("保存失败");
        }
      });
    },
    update() {
      console.log(this.form);
      this.request.post("http://localhost:9090/user/update", this.form).then(res => {
        if (res) {
          this.$message.success("修改成功");
          console.log(res);
          this.dialogFormVisible1 = false;
        } else {
          this.$message.error("修改失败");
        }
      });
    },
    cancelUpdate() {
      this.dialogFormVisible1 = false;
      this.load();
    },
    deleteUser() {
      console.log(this.form);
      this.request.post("http://localhost:9090/user/delete", this.form).then(res => {
        if (res) {
          this.$message.success("删除成功");
          console.log(res);
          this.deleteDialogFormVisible = false;
          this.load();
        } else {
          this.$message.error("删除失败");
        }
      });
    },
    handleAdd() {
      this.dialogFormVisible = true;
      this.form = {};
    },
    handleEdit(row) {
      this.form = row;
      this.dialogFormVisible1 = true;
    },
    handleDelete(row) {
      this.form = row;
      this.deleteDialogFormVisible = true;
    },
    handleSizeChange(pageSize) {
      console.log(pageSize);
      this.pageSize = pageSize;
      this.load();
    },
    handleCurrentChange(pageNum) {
      console.log(pageNum);
      this.pageNum = pageNum;
      this.load();
    }
  }
};
